import React from 'react';
import { View , Text, StyleSheet, StatusBar, Image, Dimensions,TouchableOpacity} from 'react-native'
import { connect } from 'react-redux';
import { bindActionCreators } from 'redux';
import { pressDiscover, pressCollection, pressSearch, pressProfile, openCreate } from '../../actions/mainPageActions'

class Footer extends React.Component {

    render(){
        return(
            <View style={ styles.container }>
                <View style={styles.box}>
                    <TouchableOpacity style={styles.icon} onPress={this.pressLogin}>  
                        <Image
                            style={styles.logo}
                            source = {require('./icon/home.png')}
                        /> 
                    </TouchableOpacity> 
                </View>
                <View style={styles.box}>
                    <TouchableOpacity style={styles.icon} onPress={this.pressLogin}>  
                        <Image
                            style={styles.logo}
                            source = {require('./icon/heart.png')}
                        /> 
                    </TouchableOpacity>
                </View>
                <View style={styles.box}>
                    <TouchableOpacity style={styles.icon} onPress={this.pressLogin}>  
                        <Image
                            style={styles.logo}
                            source = {require('./icon/plus.png')}
                        /> 
                    </TouchableOpacity>
                </View>
                <View style={styles.box}>
                    <TouchableOpacity style={styles.icon} onPress={this.pressLogin}>  
                        <Image
                            style={styles.logo}
                            source = {require('./icon/search.png')}
                        /> 
                    </TouchableOpacity>
                </View>
                <View style={styles.box}>
                    <TouchableOpacity style={styles.icon} onPress={this.pressLogin}>  
                        <Image
                            style={styles.logo}
                            source = {require('./icon/customer.png')}
                        /> 
                    </TouchableOpacity>
                </View>
            </View>
        )
    }
}
const deviceWitdth = Dimensions.get('window').width
const deviceHeight = Dimensions.get('window').height

const styles = StyleSheet.create({
    container:{
        position: 'absolute',
        top:deviceHeight*0.91,
        left:0,
        width:deviceWitdth,
        height: deviceHeight*0.09,
        backgroundColor:'rgba(251,195,145,0.3)',
        padding: 5,
        flexDirection: 'row',
        flexWrap: 'wrap', 

    },
    box:{
        width:'20%',
        padding:5,
        alignItems:"center",
        //justifyContent:'center',
    },
    logo:{
        width: deviceWitdth*0.07,
        height: deviceWitdth*0.07,
        top: deviceHeight*0.01,
    },
}
)
const mapStatetoProps = state =>{
    return {
        loginPageStatus : state.statusList.loginPageStatus,
        mainPageStatus : state.statusList.mainPageStatus,
        currentPage : state.statusList.currentPage,
    }
}

const mapDispatchToProps = dispatch =>
        bindActionCreators({
            pressDiscover,
            pressCollection,
            pressSearch,
            pressProfile,
            openCreate,
        },dispatch)



export default connect(mapStatetoProps,mapDispatchToProps)(Footer);
